class youtubeEmbed extends HTMLElement {
  connectedCallback() {
    this.videoId = this.getAttribute("videoid");
    this.posterUrl = `https://i.ytimg.com/vi/${this.videoId}/maxresdefault.jpg`;
    this.style.backgroundImage = `url("${this.posterUrl}")`;

    const playBtnEl = document.createElement("button");
    playBtnEl.classList.add("youtube-embed-play");
    this.append(playBtnEl);
  }
}

window.customElements.define("youtube-embed", youtubeEmbed);

class youtubeEmbed extends HTMLElement {
  connectedCallback() {
    this.videoId = this.getAttribute("videoid");
    this.posterUrl = `https://i.ytimg.com/vi/${this.videoId}/maxresdefault.jpg`;
    this.style.backgroundImage = `url("${this.posterUrl}")`;

    const playBtnEl = document.createElement("button");
    playBtnEl.classList.add("youtube-embed-play");
    this.append(playBtnEl);

    this.addEventListener("click", () => {
      const iframeEl = document.createElement("iframe");
      iframeEl.width = 560;
      iframeEl.height = 315;
      iframeEl.allow =
        "accelerometer autoplay encrypted-media gyroscope picture-in-picture";
      iframeEl.allowFullscreen = true;
      iframeEl.src = `https://www.youtube.com/embed/${this.videoId}`;
      this.append(iframeEl);
      this.classList.add("youtube-embed-ready");
      this.querySelector("iframe").focus();
    });
  }
}

window.customElements.define("youtube-embed", youtubeEmbed);

export default {
  name: "Videoplayer",
  // keeping the state 
  data() {
    return {
      playing: false,
      videoMuted: false,
    };
  },
 methods: {
    play() {
      this.$refs.player.play();
      this.setPlaying(true);
    },

    pause() {
      this.$refs.player.pause();
      this.setPlaying(false);
    },

    togglePlay() {
      if (this.playing) {
        this.pause();
      } else {
        this.play();
      }
    },

    setPlaying(state) {
      this.playing = state;
    },

    mute() {
      this.$refs.player.muted = true;
      this.setMuted(true);
    },

    unmute() {
      this.$refs.player.muted = false;
      this.setMuted(false);
    },

    toggleMute() {
      if (this.videoMuted) {
        this.unmute();
      } else {
        this.mute();
      }
    },

    setMuted(state) {
      this.videoMuted = state;
    }
  }
}

const EVENTS = [
  "play",
  "pause",
  "ended",
  "loadeddata",
  "waiting",
  "playing",
  "timeupdate",
  "canplay",
  "canplaythrough",
  "statechanged",
];

export default {
  name: "Videoplayer",
  mounted() {
    this.bindEvents();
  },
  methods: {
    bindEvents() {
      EVENTS.forEach((event) => {
        this.bindVideoEvent(event);
      });
    },

    bindVideoEvent(which) {
      const player = this.$refs.player;

      player.addEventListener(
        which,
        (event) => {
          this.$emit(which, { event, player: this });
        }
      );
    },
  }
}

data() {
  return {
    duration: 0,
  };
},
methods: {
  bindVideoEvent(which) {
    const player = this.$refs.player;

    player.addEventListener(
      which,
      (event) => {
        if (which === "loadeddata") {
          this.duration = player.duration;
        }

        this.$emit(which, { event, player: this });
      },
      true
    );
  },
}

convertTimeToDuration(seconds) {
  return [parseInt((seconds / 60) % 60, 10), parseInt(seconds % 60, 10)]
    .join(":")
    .replace(/\b(\d)\b/g, "0$1");
},
